import React, {Component} from 'react'

class Submit extends Component {
    constructor () {
        super()

        this.state = {
            input: ''
        }
    }

    render() {
        return (
            <div>
                <form>
                    <input type='text' value={this.state.value} placeholder='Name' />
                    <button type='submit' onClick={() => this.props.submitScoreButton()}>Submit Score</button>
                </form>
            </div>
        )
    }
}

export default Submit
